package com.easyshop.auth.config;import org.springframework.context.annotation.*;import org.springframework.security.config.annotation.web.builders.*;import org.springframework.security.config.http.*;import org.springframework.security.crypto.bcrypt.*;import org.springframework.security.crypto.password.*;import org.springframework.security.web.*;@Configuration public class SecurityConfig{@Bean PasswordEncoder pe(){return new BCryptPasswordEncoder();}@Bean SecurityFilterChain c(HttpSecurity h)throws Exception{h.csrf(cs->cs.disable()).sessionManagement(sm->sm.sessionCreationPolicy(SessionCreationPolicy.STATELESS)).authorizeHttpRequests(r->r.requestMatchers("/healthz","/readyz","/api/auth/login","/api/auth/register").permitAll().anyRequest().authenticated());return h.build();}}
