package com.easyshop.order.domain;import jakarta.persistence.*;import lombok.*;import java.math.*;import java.time.*;import java.util.*;@Entity @Table(name="orders",schema="easyshop") @Getter @Setter @NoArgsConstructor @AllArgsConstructor @Builder public class Order{@Id @GeneratedValue(strategy=GenerationType.IDENTITY) Long id;@Column(nullable=false) String userEmail;@Column(nullable=false) BigDecimal total;@Column(nullable=false) String status;@Column(nullable=false) Instant createdAt=Instant.now();@OneToMany(mappedBy="order",cascade=CascadeType.ALL,orphanRemoval=true,fetch=FetchType.EAGER) List<OrderItem> items=new ArrayList<>();}
